export default class Passport {
    private host;
    private merchantId;
    constructor(props: {
        host: string;
        merchantId: string;
    });
    getCaptcha(): Promise<{
        captchaId: any;
        captchaURL: string;
    }>;
    requestRegister(request: {
        regionCode: string;
        mobile: string;
        captchaId: string;
        captchaCode: string;
        email: string;
    }): Promise<any>;
    register(register: {
        name?: string;
        code: string;
        password: string;
        token: string;
    }): Promise<any>;
    requestLoginSMS(request: {
        regionCode: string;
        mobile: string;
        captchaId: string;
        captchaCode: string;
    }): Promise<any>;
    mobileLogin(login: {
        token: string;
        mobileCode: string;
    }): Promise<any>;
    login(login: {
        regionCode?: string;
        mobile?: string;
        email?: string;
        password: string;
    }): Promise<any>;
    loginWithTFA(login: {
        tfaToken: string;
        code: string;
    }): Promise<any>;
    getUserDetail(secretInfo: {
        key: string;
        secret: string;
    }): Promise<any>;
    postRequest(signData: {
        uri: any;
        body: any;
        sign?: any;
    }): Promise<any>;
    private defaulutHeader;
}
